driver = webdriver.Chrome()
wait = WebDriverWait(driver, 10)
v_category = "음악"
for x in links:
    driver.get(x)
    v_id = x.strip('https://www.youtube.com/watch?v=')
    v_title = wait.until(EC.presence_of_element_located(
        (By.CSS_SELECTOR, "h1.title yt-formatted-string"))).text
    v_description = wait.until(EC.presence_of_element_located(
        (By.CSS_SELECTOR, "div#description yt-formatted-string"))).text
    v_like =wait.until(EC.presence_of_element_located(
        (By.CSS_SELECTOR, " div#top-level-buttons ytd-toggle-button-renderer:nth-child(1)"))).text
    v_dislike=wait.until(EC.presence_of_element_located(
        (By.CSS_SELECTOR, " div#top-level-buttons ytd-toggle-button-renderer:nth-child(2)"))).text
    v_channel=wait.until(EC.presence_of_element_located(
        (By.CSS_SELECTOR, "div#text-container yt-formatted-string#text a"))).text
    df.loc[len(df)] = [v_id, v_title, v_description, v_category,v_like,v_dislike,v_channel]

